function GivemeaDiamond(r){if(r<1||r%2!==1)return null;if(r>2&&r%2===1){var n=Math.floor(r/2);return Array.apply(null,Array(r)).map(function(t,e){return" ".repeat(Math.abs(n-e))+"*".repeat(r-2*Math.abs(n-e))+"\n";
}).join("")}}function persistence(r){function n(r){return r.toString().split("").reduce(function(r,n){return r*parseInt(n)},1)}for(var t=0;r>9;)r=n(r),t++;return t;
}